(self.webpackChunktui_editor=self.webpackChunktui_editor||[]).push([[6568],{26568:t=>{t.exports='<tui-editor\n    [formControl]="control"\n    [tools]="builtInTools"\n>\n    <ng-container ngProjectAs="mention">\n        <tui-data-list>\n            <button\n                *ngFor="let item of getFilteredItems(items, this.wysiwyg?.mentionSuggestions)"\n                tuiOption\n                (click)="setMention(item)"\n            >\n                {{ item.name }}\n                <tui-avatar\n                    size="s"\n                    [avatarUrl]="item.avatar"\n                    [rounded]="true"\n                    [text]="item.name"\n                ></tui-avatar>\n            </button>\n        </tui-data-list>\n    </ng-container>\n</tui-editor>\n\n<h4>HTML:</h4>\n<tui-editor-socket [content]="control.value || \'\'"></tui-editor-socket>\n\n<h4>Text:</h4>\n<p>{{ control.value }}</p>\n'}}]);